:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:49:\"http://pluginsforwp.net/plugins/wp-video-gallery/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"http://pluginsforwp.net/plugins/wp-video-gallery/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sun, 22 Jun 2014 03:12:20 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Plugins\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"http://pluginsforwp.net/?p=180\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:345:\"WP Video Gallery is the Powerful WordPress Plugin. You can easily manage your videos and Images of Youtube, Vimeo, Dailymotion, Metacafe, Twitpic, and Instagram (Photos). You can create your gallery of your list of Video or Photo. Easily control it from Backend. WP Video Gallery has light box Effect to show your video quickly and Responsive.\r\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"zafar\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:13366:\"      \r\n<div id=\'category_name\'><div id=\'category_wrapper\'><h2>Color of Natural</h2></div><div class=\'slider4\'>\r\n		\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/22439234\" title=\"The Mountain\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/1.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>The Mountain</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/14074949\" title=\"Dark Side of the Lens\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/2.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>Dark Side of the Lens</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/21294655\" title=\"The Aurora\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/3.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>The Aurora</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/35396305\" title=\"Yosemite HD\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/4.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>Yosemite HD</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/11673745\" title=\"Iceland\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/5.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>Iceland</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/29950141\" title=\"Landscapes Volume 2\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/6.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>Landscapes Volume 2</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://vimeo.com/24456787\" title=\"The Arctic Light\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/7.jpg\" /><small class=\"play_area\" style=\"background-image:url(\'http://pluginsforwp.net/wp-content/plugins/wp-video-gallery/includes/images/standard-play.png\');background-position:center;\"></small></a>\r\n        <h4>The Arctic Light</h4>\r\n        </div>\r\n        \r\n	</div></div>\n      \r\n<div id=\'category_name\'><div id=\'category_wrapper\'><h2>Best Photos</h2></div><div class=\'slider4\'>\r\n		\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image1-large.jpg\" title=\"Green Place\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image1.jpg\" /></a>\r\n        <h4>Green Place</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image2-large.jpg\" title=\"Sunset\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image2.jpg\" /></a>\r\n        <h4>Sunset</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image3-large.jpg\" title=\"Parots\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image3.jpg\" /></a>\r\n        <h4>Parots</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image4-large.jpg\" title=\"Green Mountains\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image4.jpg\" /></a>\r\n        <h4>Green Mountains</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image5-large.jpg\" title=\"Leaves\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image5.jpg\" /></a>\r\n        <h4>Leaves</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/images6-large.jpg\" title=\"Tower\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/images6.jpg\" /></a>\r\n        <h4>Tower</h4>\r\n        </div>\r\n        \r\n			\r\n        <div class=\"slide\"><a class=\"vgimage\" href=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image7-large.jpg\" title=\"Cars\"><img src=\"http://pluginsforwp.net/wp-content/uploads/2014/06/image7.jpg\" /></a>\r\n        <h4>Cars</h4>\r\n        </div>\r\n        \r\n	</div></div>\n<div id=\"symple-tab-10\" class=\"symple-tabs\"><ul class=\"ui-tabs-nav symple-clearfix\"><li><a href=\"#symple-tab-description\">Description</a></li><li><a href=\"#symple-tab-installation\">Installation</a></li><li><a href=\"#symple-tab-faq\">FAQ</a></li><li><a href=\"#symple-tab-screenshot\">Screenshot</a></li><li><a href=\"#symple-tab-changelog\">Changelog</a></li></ul>\n<div id=\"symple-tab-description\" class=\"tab-content  symple-all\">\n<div class=\"block-content\">\n<p><span style=\"font-size: 12pt;\">WP Video Gallery is the Powerful WordPress Plugin. You can easily manage your videos and Images of Youtube, Vimeo, Dailymotion, Metacafe, Twitpic, and Instagram (Photos). You can create your gallery of your list of Video or Photo. Easily control it from Backend. WP Video Gallery has light box Effect to show your video quickly and Responsive.</span></p>\n<h3><span style=\"font-size: 12pt;\">Features Included:</span></h3>\n<ul>\n<li><span style=\"font-size: 12pt;\">Unlimited Video Categories</span></li>\n<li><span style=\"font-size: 12pt;\">Unlimited Video Slides</span></li>\n<li><span style=\"font-size: 12pt;\">Responsive and Mobile Touch</span></li>\n<li><span style=\"font-size: 12pt;\">Pagination and Control Navigation</span></li>\n<li><span style=\"font-size: 12pt;\">Easily Add Link of Media Popular Sites like. Youtube, Vimeo, MetaCafe, Instagram, and Images</span></li>\n<li><span style=\"font-size: 12pt;\">Custom Thumbnail Supported</span></li>\n<li><span style=\"font-size: 12pt;\">Custom Video Title. Gallery Title</span></li>\n<li><span style=\"font-size: 12pt;\">Facebook and Twitter Share Support</span></li>\n<li><span style=\"font-size: 12pt;\">Supported all Modern Browsers</span></li>\n<li><span style=\"font-size: 12pt;\">Play Video in Lightbox</span></li>\n<li><span style=\"font-size: 12pt;\">Play Button</span></li>\n<li><span style=\"font-size: 12pt;\">Flexible Video Player Size</span></li>\n<li><span style=\"font-size: 12pt;\">Manage Number of Videos to Show (Min) and (Max)</span></li>\n<li><span style=\"font-size: 12pt;\">Fully Manageable from WordPress Admin</span></li>\n<li><span style=\"font-size: 12pt;\">and many more to come!</span></li>\n</ul>\n</div>\n</div>\n<div id=\"symple-tab-installation\" class=\"tab-content  symple-all\">\n<div class=\"block-content\">\n<p><span style=\"font-size: 12pt;\"><strong>Installation of WP Video Gallery Plugin is Easy!.</strong></span></p>\n<ol>\n<li><span style=\"font-size: 12pt;\">Upload <code>wp-video-gallery</code> directory to the <code>/wp-content/plugins/</code> directory</span></li>\n<li><span style=\"font-size: 12pt;\">Activate the plugin through the &#8216;Plugins&#8217; menu in WordPress</span></li>\n<li><span style=\"font-size: 12pt;\">Place <code>&lt;?php if( function_exists(\'wpvg_get_all_categories\') ) wpvg_get_all_categories(); ?&gt;</code> in your templates or use shortcode &#8220;<code>[*video_categories*]</code>&#8221; (remove stars).<br />\n</span></li>\n</ol>\n<p><span style=\"font-size: 12pt;\">4.That&#8217;s it. You&#8217;re ready to go!</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Minimal Requirements</strong></span></p>\n<p><span style=\"font-size: 12pt;\">PHP: 5.x.x mySQL: 4.0, 4.1 or 5.x WordPress: 3.4 or newer</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Recommended Requirements</strong></span></p>\n<p><span style=\"font-size: 12pt;\">PHP: 5.2.x or newer mySQL: 5.x WordPress: 3.9 or newer</span></p>\n</div>\n<p>&nbsp;</p>\n</div>\n<div id=\"symple-tab-faq\" class=\"tab-content  symple-all\">\n<div class=\"block-content\">\n<h4><span style=\"font-size: 12pt;\">How to Display Individual Gallery?</span></h4>\n<p><span style=\"font-size: 12pt;\">You Can Get Individual Video Gallery in two ways. 1. Use Shortcode with ID &#8220;<code>[*gallery_videos id=\"1\" *]</code>&#8221; (remove stars). to your WordPress Page or Post 2. Place the Code with your Gallery ID in Template Pages. like index.php or single.php</span></p>\n<h4><span style=\"font-size: 12pt;\">How to Get All Videos?</span></h4>\n<p><span style=\"font-size: 12pt;\">You can get Video Gallery in two ways. 1. Use Shortcode &#8220;<code>[*video_categories*]</code>&#8221; (remove stars) to your WordPress Page or Post. 2. Place the Code in Template Pages. like index.php or single.php</span></p>\n<h4><span style=\"font-size: 12pt;\">Which videos URL i can use?</span></h4>\n<p><span style=\"font-size: 12pt;\">Currently WP Video Gallery Supported the Following Video Media&#8217;s Youtube, DailyMotion, Vimeo, MetaCafe, Twitvid, Twitpic and Instagram and Images URL.</span></p>\n<h4><span style=\"font-size: 12pt;\">How can I change the Title of Video Category?</span></h4>\n<p><span style=\"font-size: 12pt;\">We are working on this Feature. Soon I will be available in next upgrades</span></p>\n<h4><span style=\"font-size: 12pt;\">I lost my Wp Video Gallery Settings How can I get default settings?</span></h4>\n<p><span style=\"font-size: 12pt;\">You can Reset you wp video gallery setting by deactivate the plugin and reactivate it.</span></p>\n<h4><span style=\"font-size: 12pt;\">How Can I get the WP Video Gallery Plugin customized to my needs?</span></h4>\n<p><span style=\"font-size: 12pt;\">Yes, but there will be additional charges based on the request for customization has to be made by the requester.</span></p>\n<h4><span style=\"font-size: 12pt;\">How Can I Contact the Support / Developement Team of WP Video Gallery.</span></h4>\n<p><span style=\"font-size: 12pt;\">You can contact us for support and developement. on Email: <a href=\"mailto:webmaster.gulzarahmed@gmail.com\" rel=\"nofollow\">webmaster.gulzarahmed@gmail.com</a></span></p>\n<h4><span style=\"font-size: 12pt;\">WP Video Gallery is not Working?</span></h4>\n<p><span style=\"font-size: 12pt;\">WP Video Gallery is Required JQuery 1.11 or JQuery 1.7.2 it is also compitable with stable jqueries. this plugin has already jquery included. Just make sure that other plugins are not conflicting with jquery or the plugin.</span></p>\n<h4><span style=\"font-size: 12pt;\">is WP Video Gallery Premium Version is Available?</span></h4>\n<p><span style=\"font-size: 12pt;\">Our Team is Working and creating WP Video Gallery Premuim Version to Extend More Power.</span></p>\n<h4><span style=\"font-size: 12pt;\">How can i replace the play button?</span></h4>\n<p><span style=\"font-size: 12pt;\">You can replace the Play Button Image on wp-video-gallery/includes/images &#8220;play_small_button.png&#8221;(Replace it).</span></p>\n</div>\n</div>\n<div id=\"symple-tab-screenshot\" class=\"tab-content  symple-all\">\n<p>&nbsp;</p>\n<ol class=\"screenshots\">\n<li><a title=\"Click to view full-size screenshot 1\" href=\"//s.w.org/plugins/wp-video-gallery/screenshot-1.png?r=938917\" rel=\"nofollow\"><img class=\"screenshot\" src=\"//s.w.org/plugins/wp-video-gallery/screenshot-1.png?r=938917\" alt=\"wp-video-gallery screenshot 1\" /></a>WP Video Gallery Videos and Images.</li>\n<li><a title=\"Click to view full-size screenshot 2\" href=\"//s.w.org/plugins/wp-video-gallery/screenshot-2.png?r=938917\" rel=\"nofollow\"><img class=\"screenshot\" src=\"//s.w.org/plugins/wp-video-gallery/screenshot-2.png?r=938917\" alt=\"wp-video-gallery screenshot 2\" /></a>The Lightbox of Video Player</li>\n<li><a title=\"Click to view full-size screenshot 3\" href=\"//s.w.org/plugins/wp-video-gallery/screenshot-3.png?r=938917\" rel=\"nofollow\"><img class=\"screenshot\" src=\"//s.w.org/plugins/wp-video-gallery/screenshot-3.png?r=938917\" alt=\"wp-video-gallery screenshot 3\" /></a></li>\n<li>The Lightbox of Image</li>\n<li><a title=\"Click to view full-size screenshot 4\" href=\"//s.w.org/plugins/wp-video-gallery/screenshot-4.png?r=938917\" rel=\"nofollow\"><img class=\"screenshot\" src=\"//s.w.org/plugins/wp-video-gallery/screenshot-4.png?r=938917\" alt=\"wp-video-gallery screenshot 4\" /></a>Wp Video Gallery (Category) Option Page</li>\n</ol>\n</div>\n<div id=\"symple-tab-changelog\" class=\"tab-content  symple-all\">\n<div class=\"block-content\">\n<h4><span style=\"font-size: 12pt;\">0.6</span></h4>\n<ul>\n<li><span style=\"font-size: 12pt;\">Add Facebook and Twitter Share</span></li>\n</ul>\n<h4><span style=\"font-size: 12pt;\">0.5</span></h4>\n<ul>\n<li><span style=\"font-size: 12pt;\">Add the Fingre Touch (Mobile Support)</span></li>\n<li><span style=\"font-size: 12pt;\">Make the Video Gallery Responsive.</span></li>\n</ul>\n</div>\n</div>\n</div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:54:\"http://pluginsforwp.net/plugins/wp-video-gallery/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"1\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:62:\"\n		\n		\n		\n		\n		\n				\n\n		\n		\n	    \n    \n	\n	\n	\n	\n	\n			\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:18:\"WP Carousel Slider\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:3:{i:0;a:5:{s:4:\"data\";s:51:\"http://pluginsforwp.net/plugins/wp-carousel-slider/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:4:{s:3:\"rel\";s:10:\"stylesheet\";s:4:\"href\";s:75:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/css/style.css\";s:4:\"type\";s:8:\"text/css\";s:5:\"media\";s:6:\"screen\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:4:{s:3:\"rel\";s:10:\"stylesheet\";s:4:\"href\";s:78:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/css/slimbox2.css\";s:4:\"type\";s:8:\"text/css\";s:5:\"media\";s:6:\"screen\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:60:\"http://pluginsforwp.net/plugins/wp-carousel-slider/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 09 Oct 2012 08:24:46 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Plugins\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"http://pluginsforwp.net/?p=76\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:235:\"WP carousel slider is powerful jQuery based wordpress carousel slider. It has left and right rotations directions. Easy navigation allow you to rotate slides in left and right the directions, Slides reflection and mouse wheel rotation.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:6:\"script\";a:5:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:2:{s:4:\"type\";s:15:\"text/javascript\";s:3:\"src\";s:78:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/js/jquery.min.js\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:2:{s:4:\"type\";s:15:\"text/JavaScript\";s:3:\"src\";s:85:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/js/jquery.mousewheel.js\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:2:{s:4:\"type\";s:15:\"text/JavaScript\";s:3:\"src\";s:76:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/js/slimbox2.js\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:2:{s:4:\"type\";s:15:\"text/JavaScript\";s:3:\"src\";s:88:\"http://pluginsforwp.net/wp-content/plugins/wp-carousel-slider/js/cloud-carousel.1.0.5.js\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:306:\"\n$(document).ready(function(){\n	$(\"#images_move\").CloudCarousel( { \n		reflHeight: 56,\n		reflGap:2,\n		titleBox: $(\'#da-vinci-title\'),\n	altBox: $(\'#da-vinci-alt\'),	yRadius:50,\n 	xRadius:300,\n	autoRotate: \'left\',\n	xPos: 360,\n	yPos: 120,\n	speed:0.2,\n	mouseWheel:true,\n	autoRotateDelay: 3000,\n	FPS:30,\n	});\n});\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:4:\"type\";s:15:\"text/javascript\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"zafar\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:6784:\"<p style=\"text-align: center;\">	<div id=\"images_move\" style=\" margin:0 auto; width:720px; height:500px; overflow:scroll;\">\n \n   <a href=\"\" rel=\'lightbox\' title=\"Mountains and River\"><img class=\"cloudcarousel\" src=\"http://pluginsforwp.net/wp-content/uploads/2012/06/Nature-300x225.jpg\" alt=\"Mountains and River\" title=\"Nature\" /></a> \n  <a href=\"http://pluginsforwp.net/wp-content/uploads/2012/06/Bird.jpg\" rel=\'lightbox\' title=\"Bird is looking\"><img class=\"cloudcarousel\" src=\"http://pluginsforwp.net/wp-content/uploads/2012/06/Bird-300x225.jpg\" alt=\"Bird is looking\" title=\"Bird\" /></a>\n  <a href=\'http://pluginsforwp.net/wp-content/uploads/2012/06/abstract.jpg\' rel=\'lightbox\' title=\"Tree and grass\"><img class=\"cloudcarousel\" src=\"http://pluginsforwp.net/wp-content/uploads/2012/06/abstract-300x225.jpg\" alt=\"Tree and grass\" title=\"Tree\" /></a>\n  <a href=\'http://pluginsforwp.net/wp-content/uploads/2012/06/Car.jpg\' rel=\'lightbox\' title=\"A red car in showcase\"><img class=\"cloudcarousel\" src=\"http://pluginsforwp.net/wp-content/uploads/2012/06/Car-300x225.jpg\" alt=\"A red car in showcase\" title=\"Car\" /></a>\n<h2 id=\"da-vinci-title\"></h2>\n<h3 id=\"da-vinci-alt\"></h3>\n<div id=\"but1\" class=\"carouselLeft\" style=\"position:absolute;top:20px;right:60px;\"></div>\n<div id=\"but2\" class=\"carouselRight\" style=\"position:absolute;top:20px;right:20px;\"></div>      \n</div>\n\n<div id=\"symple-tab-78\" class=\"symple-tabs\"><ul class=\"ui-tabs-nav symple-clearfix\"><li><a href=\"#symple-tab-description\">Description</a></li><li><a href=\"#symple-tab-installation\">Installation</a></li><li><a href=\"#symple-tab-faq\">FAQ</a></li><li><a href=\"#symple-tab-screenshot\">Screenshot</a></li><li><a href=\"#symple-tab-changelog\">Changelog</a></li></ul>\n<div id=\"symple-tab-description\" class=\"tab-content  symple-all\">\n<p><span style=\"font-size: 12pt;\">WP Carousel Slider is Jquery based WordPress. It have reflection mouse wheel rotation and easy to navigate.</span><br />\n<span style=\"font-size: 12pt;\"> Slider structure is seo friendly and Light box image effect.</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Options</strong></span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Slider Width</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the width of slider as you want. By Default is [720]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Slider Height</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the height of slider as you want. By Default is [500]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Horizontal Position</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the Images rotation orign point as according to slider width. By Default is [300]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Vertical Raduis</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the Radius of images in order to set close or far space between images. By Default is [300]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Speed</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the rotation speed of slides images as you want. By Defualt is [3000]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Rotation Delay</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the Rotation delay in order to hold/pause images time. By Defualt is [Disabled]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Mouse Wheel</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can enable or disable mouse wheel rotation. By Defualt is [Disabled]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Rotation Direction</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can set the rotation direction of images to left or right. By Defualt is [Left]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Navigation Buttons</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can enable or disable navigation buttons. By Defualt is [Disabled]</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Title and Description</strong></span><br />\n<span style=\"font-size: 12pt;\"> You can enable or disable Title and Description. By Defualt is [Disabled]</span></p>\n</div>\n<div id=\"symple-tab-installation\" class=\"tab-content  symple-all\">\n<p><span style=\"font-size: 12pt;\"><strong>Installation</strong></span></p>\n<p><span style=\"font-size: 12pt;\">Extract the wp-carousel-slider.zip and Upload it on the Plugins Directory (WordPress Plugin Directory) and activate Plugin.</span><br />\n<span style=\"font-size: 12pt;\"> or</span><br />\n<span style=\"font-size: 12pt;\"> Go to your WordPress Plugins &gt; Add New and Upload your wp-carousel-slider.zip.</span><br />\n<span style=\"font-size: 12pt;\"> <strong>Requirements</strong></span><br />\n<span style=\"font-size: 12pt;\"> Require at lease 3.0 and up.</span><br />\n<span style=\"font-size: 12pt;\"> Tasted up to 3.4.2</span><br />\n<span style=\"font-size: 12pt;\"> Usages</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>There are two ways to use slider</strong></span><br />\n<span style=\"font-size: 12pt;\"> 1. Use the shortcode [*wp-carousel*] (remove stars). in the content area of a page or post where you want the slider to appear.</span><br />\n<span style=\"font-size: 12pt;\"> 2. Use echo do_shortcode(&#8216;[*wp-carousel*]&#8217;); (remove stars) in you theme page or post PHP file.</span></p>\n</div>\n<div id=\"symple-tab-faq\" class=\"tab-content  symple-all\">\n<p><span style=\"font-size: 12pt;\"><strong>How can I use this Plugin?</strong></span></p>\n<p><span style=\"font-size: 12pt;\">You can use this plugin via Shortcode [*wp-carousel*] (remove stars) in the pages and posts.</span></p>\n<p><span style=\"font-size: 12pt;\">= Plugin is activate but does not see the images? =</span></p>\n<p><span style=\"font-size: 12pt;\">Please Add the Images in the Slider from WP Carousel Page in WordPress Dasboard</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>Slider is not rotating properly?</strong></span></p>\n<p><span style=\"font-size: 12pt;\">Please make sure that the slider settings are match with slider perameters. if still problem so leave blank fields of Carousel slider settings in order to use Default settings.</span><br />\n</div>\n<div id=\"symple-tab-screenshot\" class=\"tab-content  symple-all\">\nSee Above Working Demo<br />\n</div>\n<div id=\"symple-tab-changelog\" class=\"tab-content  symple-all\">\n<p><span style=\"font-size: 12pt;\"><strong>0.6</strong></span><br />\n<span style=\"font-size: 12pt;\"> A change since the previous version.</span><br />\n<span style=\"font-size: 12pt;\"> Add Title and Description</span><br />\n<span style=\"font-size: 12pt;\"> Add Left and Right rotations directions</span></p>\n<p><span style=\"font-size: 12pt;\"><strong>0.5</strong></span><br />\n<span style=\"font-size: 12pt;\"> Add Raduis of images</span></p>\n</div>\n</div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:56:\"http://pluginsforwp.net/plugins/wp-carousel-slider/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:4:\"href\";s:29:\"http://pluginsforwp.net/feed/\";s:3:\"rel\";s:4:\"self\";s:4:\"type\";s:19:\"application/rss+xml\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:44:\"http://purl.org/rss/1.0/modules/syndication/\";a:2:{s:12:\"updatePeriod\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"hourly\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:15:\"updateFrequency\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"1\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";a:11:{s:4:\"date\";s:29:\"Sun, 23 Nov 2014 21:46:14 GMT\";s:6:\"server\";s:6:\"Apache\";s:10:\"x-pingback\";s:34:\"http://pluginsforwp.net/xmlrpc.php\";s:4:\"etag\";s:34:\"\"22ac830323490c897d1d17f9d3835457\"\";s:12:\"x-robots-tag\";s:14:\"noindex,follow\";s:13:\"last-modified\";s:29:\"Wed, 13 Aug 2014 07:53:36 GMT\";s:4:\"vary\";s:15:\"Accept-Encoding\";s:16:\"content-encoding\";s:4:\"gzip\";s:14:\"content-length\";s:4:\"5779\";s:10:\